import 'bootstrap/dist/css/bootstrap.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';


function HomeCard(props) {
  return (
    <Card className="border-0 mt-5 bg-dark">
      <Row className="">
        {props.image ?
          (<Col md={6} className={(props.imageLeft ? "order-1" : "order-0")}>
            <a href={props.buttonHref}>
              <Card.Img variant="left w-100 h-100" src={props.image} />
            </a>
          </Col>) : (null)
        }
        <Col md={(props.image ? 6 : 12)}>
          <Card.Body className="text-light">
            <Card.Title className="display-6 fw-bold text-center mb-5">{props.title}</Card.Title>
            <Card.Text>
              {props.text}
            </Card.Text>
            <Button variant="warning" href={props.buttonHref} size="lg">{props.buttonText}</Button>
          </Card.Body>
        </Col>
      </Row>
    </Card>
  );
}

export default HomeCard;
