import 'bootstrap/dist/css/bootstrap.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';


function HomeCard(props) {
  return (
    <Container className={(props.image ? "" : "w-50")}>
      <Card className="border-0 mt-5 bg-dark" >
        <Row>
          {props.image ?
            (<Col md={6} className={(props.imageLeft ? "order-1" : "order-0")}>
              <a href={props.buttonHref}>
                <Card.Img variant="left w-100 h-100" src={props.image} />
              </a>
            </Col>) : (null)
          }
          <Col md={(props.image ? 6 : 12)}>
            <Card.Body className="text-light">
              <Card.Title className="display-6 fw-bold text-center mb-5">{props.title}</Card.Title>
              <Card.Text className={"h4" + (props.image ? "" : "text-center")}>
                {props.text}
              </Card.Text>
              <Row>
                <Col className={(props.image ? "" : "text-center")}>
                  <Button variant="warning" href={props.buttonHref} size="lg">{props.buttonText}</Button>
                </Col>
              </Row>
            </Card.Body>
          </Col>
        </Row>
      </Card>
    </Container>
  );
}

export default HomeCard;
