import 'bootstrap/dist/css/bootstrap.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';


function HomeCard(props) {
  return (
    <Card className="border-0 mt-5 bg-dark">
      <Row className="">
        <Col sm={6} className={(props.imageLeft ? "order-0" : "order-1")}>
          <Card.Img variant="left w-100 h-100" src={props.image} />
        </Col>
        <Col sm={6}>
          <Card.Body className="text-light">
            <Card.Title className="display-5 fw-bold text-center mb-5">{props.title}</Card.Title>
            <Card.Text>
              {props.text}
            </Card.Text>
            <Button variant="warning" href={props.buttonHref}>{props.buttonText}</Button>
          </Card.Body>
        </Col>
      </Row>
    </Card>
  );
}

export default HomeCard;
